def decimal_to_binary(n):
    return "{0:b}".format(int(n))


def add(plaintext, stream):
    result = []
    for i in range(len(plaintext)):
        result.append((plaintext[i] + stream[i]) % 2)
    return result


def next_bit(polynomial, state):
    total = 0
    for i in range(len(polynomial)):
        if polynomial[i] == 1:
            total += state[i]
    return total % 2


def lfsr(plaintext, polynomial, stream):
    while len(stream) < len(plaintext):
        bit = next_bit(polynomial, stream[-(len(polynomial)):])
        stream.append(bit)

    return add(plaintext, stream)


def geffe(key1, key2, key3, plaintext):
    stream = []
    stream1 = key1.copy()
    while len(stream) < len(plaintext)-5:
        x1 = next_bit(p1, key1)
        stream1.append(x1)
        key1 = key1[1:] + [x1]
        stream.append(x1)
    stream = []
    stream2 = key2.copy()
    while len(stream) < len(plaintext)-7:
        x2 = next_bit(p2, key2)
        stream2.append(x2)
        key2 = key2[1:] + [x2]
        stream.append(x2)
    stream = []
    stream3 = key3.copy()
    while len(stream) < len(plaintext)-11:
        x3 = next_bit(p3, key3)
        stream3.append(x3)
        key3 = key3[1:] + [x3]
        stream.append(x3)
    stream = []
    for i in range(len(plaintext)):
        stream.append((stream1[i]*stream2[i] + stream2[i]*stream3[i] + stream3[i]) % 2)

    return add(stream, plaintext)


def find_match_ratio(arr1, arr2):
    counter = 0
    for i in range(len(arr1)):
        if arr1[i] == arr2[i]:
            counter += 1
    return counter / len(arr1)

